/* Local shim loader for Choices.js: dynamically load CDN script to avoid corrupted local file issues.
   Placing this file at /assets/vendor/choices/choices.min.js preserves existing local paths while
   delegating actual script loading to the CDN. */
(function () {
  try {
    if (typeof window !== "undefined" && typeof Choices === "undefined") {
      var s = document.createElement("script");
      s.src =
        "https://cdn.jsdelivr.net/npm/choices.js/public/assets/scripts/choices.min.js";
      s.async = false;
      s.defer = false;
      s.onload = function () {
        // noop
      };
      s.onerror = function () {
        console.error("Failed to load Choices.js from CDN via local shim");
      };
      document.head.appendChild(s);
    }
  } catch (e) {}
})();
